import { Injectable } from '@angular/core';
import { v4 as uuid } from 'uuid';
import { UserLogin, UserSession } from '../interfaces/users.interface';

@Injectable({
  providedIn: 'root'
})
export class SessionService {

  public storageUserSession: UserSession[] = [];

  public userSession: UserSession = {
    userId: '',
    username: '',
    role: false
  };

  constructor() {
    this._loadUserFromStorage();
  }

  private _loadUserFromStorage():void {
    console.log('_loadUserFromStorage()');

    if (!localStorage.getItem('storageUsersSessions')) return;

    this.storageUserSession = JSON.parse(localStorage.getItem('storageUsersSessions')!)
  }

  private _saveUserToStorage():void {
    console.log('_saveUserToStorage()');

    localStorage.setItem('storageUsersSessions', JSON.stringify(this.userSession));
  }

  private _setUserSession(userData:UserSession):void {
    console.log('_setUserSession()');

    this.userSession = {...userData };
  }

  private _removeUserSession(username:string):void {
    console.log('_removeUserSession()');

    this.storageUserSession = this.storageUserSession.filter(storageUsers => storageUsers.username !== username);
  }

  public login(userLogin:UserLogin):void {
    console.log(userLogin);

    if (!this.userSession.userId) {
      this._setUserSession(userLogin);
      this._saveUserToStorage();
    }

    this._setUserSession({...userLogin});

    console.log(this.userSession);
  }

  public logout(username: string):void {
    this._removeUserSession(username);
  }
}
