import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Status, ToDo } from '../../../interfaces/todos.interface';
import { TasksService } from '../../../services/tasks.service';

@Component({
  selector: 'task-lists',
  templateUrl: './list-tasks.component.html'
})
export class TasksListsComponent {

  constructor () {}

  @Input()
  public newTodos: ToDo[] = [];

  @Input()
  public deletedTodos: ToDo[] = [];

  @Output()
  public onDeleteTodo: EventEmitter<string> = new EventEmitter();

  @Output()
  public onDeleteAll: EventEmitter<string> = new EventEmitter();

  @Output()
  public onUpdateToDo: EventEmitter<{idTodo: string, status: string}> = new EventEmitter();


  removeToDo(id?: string): void {
    if (!id) return;
    this.onDeleteTodo.emit(id);
  }

  removeToDoAll(): void {
    this.onDeleteAll.emit();
  }

  updateStatusTodo(idTodo: string, status: string): void {
    console.log(idTodo, status);

    if (!idTodo || !status) return;
    this.onUpdateToDo.emit({idTodo, status});
  }
}
