import { Component} from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

import { UserSession } from 'src/app/interfaces/users.interface';
import { SessionService } from '../../services/session.service';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public loginForm:FormGroup = new FormGroup({});
  public 

  public isSubmitted:boolean = false;
  public isValid: boolean = false;
  public messageError:string = '';

  constructor(
    public fb:FormBuilder
    private readonly sessionService: SessionService
  ) {}

  ngOnInit(): void {
    this.loginForm = new FormGroup(
      {
        email: new FormControl('', [
          Validators.required,
          Validators.email
        ]),
        password: new FormControl('', [
          Validators.required,
          Validators.minLength(5),
          Validators.maxLength(8)
        ])
      }
    );
  }

  onLogin():void{
    const dataForm    = this.loginForm.value;
    let   errorCount  = 0;
    this.isSubmitted  = true;
    this.messageError = '';

    if (dataForm.username === '') {
      this.messageError += '- Debe agregar su "Nombre de usuario".<br>' ;
      errorCount++;
    }
    if (dataForm.password === '') {
      this.messageError += '- Debe agregar su "Contraseña".<br>';
      errorCount++;
    }
    if (dataForm === '') {
      this.messageError += '- Debe selecionar una opción de "Rol"<br>';
      errorCount++;
    }

    if (dataForm.invalid || errorCount > 0) return;

    this.sessionService.login(dataForm);
  }
}
