import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

import { environment } from 'src/environments/environment.development';
import { v4 as uuid } from 'uuid';
import { User } from '../interfaces/users.interface';
import { Observable, catchError, of, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  public apiUrl = environment.apiUrl;

  constructor(private http:HttpClient, private router:Router ) {}

  get getToken() {
    return localStorage.getItem('auth_token');
  }

  get isLoggedIn(): boolean {
    let authToken = localStorage.getItem('auth_token');
    console.log('- isLoggedIn: ', authToken);

    return authToken !== null;
  }

  login(userDataForm: User):Observable<any> {
    let userEmail = userDataForm.email.split('@');

    return this.http.post(`${this.apiUrl}/users/`, { token: uuid(), username: userEmail[0],  ...userDataForm })
      .pipe(
        tap((users:any) => {
          console.log('- User Post Login', users);

          const { authToken } = users;

          localStorage.setItem('auth_token', authToken.token);
        }),
        catchError(() => {
          console.log('- Error al autenticar usuario...');
          return of([])
        })
      )
  }

