import { Component} from '@angular/core';

import { SessionService } from '../../services/session.service';
import { UserLogin, UserSession } from 'src/app/interfaces/users.interface';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public userSession: UserSession[] = [];
  public isSubmitted:boolean = false;
  public isValid: boolean = false;
  public messageError:string = '';

  constructor(private readonly sessionService: SessionService) {}

  onSubmit(loginForm: NgForm): boolean{
    this.isSubmitted = true;
    console.log(loginForm.value);

    this.messageError += (loginForm.value.username !== '') ? '' : ' - Debe agregar un "Username".';
    this.messageError += (loginForm.value.role !== '') ? '' : '<br> - Debe selecionar una opción de "Rol"';
    this.messageError += '<br>';

    return (loginForm.invalid ?? this.messageError.length === 0);
  }
}
