import { Component} from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';

import { SessionService } from '../../services/session.service';
import { UserSession } from 'src/app/interfaces/users.interface';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public userSession: UserSession[] = [];
  public isSubmitted:boolean = false;
  public isValid: boolean = false;
  public loginForm: any = '';

  constructor(public fb:FormBuilder, private readonly sessionService: SessionService) {
    loginForm = this.fb.group({
      role: ['roleUser', [Validators.required]]
    })
  }

  public get myForm() {
    return this.loginForm.get('role')
  }

  public get isLoggin() {
    return this.sessionService.storageUserSession;
  }

  onSubmit(){
    this.isSubmitted = true;

  }
}
