import { Injectable } from '@angular/core';
import { v4 as uuid } from 'uuid';

import { ToDo } from '../interfaces/todos.interface';

@Injectable({
  providedIn: 'root'
})
export class TasksService {

  public storageTasks: ToDo[] = [
    {
      idTodo: uuid(),
      username: 'Aarón García',
      nameTodo: 'Tarea 1',
      status: 'new',
      dateInitTodo: new Date()
    }
  ];

  public idTodo?: string;
  public username: string = '';
  public nameTodo: string = '';
  public status?: 'new' | 'end' | 'delete' | '';
  public dateInitTodo?: Date = new Date();
  public dateEndTodo?: Date;


  constructor() {
    this.loadFromLocalStorage()
  }

  private loadFromLocalStorage() {
    if (!localStorage.getItem('storageTasks')) return;

    this.storageTasks = JSON.parse(localStorage.getItem('storageTasks')!)
  }

  private saveToLocalStorage() {
    localStorage.setItem('storageTasks', JSON.stringify(this.storageTasks));
    console.log('- saveToLocalStorage', this.storageTasks);
  }

  public resetValues():void {
    this.idTodo    = '';
    this.username  = '';
    this.nameTodo  = '';
    this.status    = '';
    this.dateInitTodo = new Date();
    this.dateEndTodo = undefined;
  }

  public addTask(task: ToDo): void {
    this.storageTasks.push({idTodo: uuid(), dateInitTodo: new Date(), ...task})

    this.saveToLocalStorage();
  }
}
