import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { Observable, catchError, of, tap } from 'rxjs';

import { v4 as uuid } from 'uuid';

import { environment } from 'src/environments/environment.development';
import { User } from '../interfaces/users.interface';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  public apiUrl:string = environment.apiUrl;

  constructor(private http:HttpClient, private router:Router ) {}

  get getToken() {
    return localStorage.getItem('auth_token');
  }

  get isLoggedIn(): boolean {
    let authToken = localStorage.getItem('auth_token');
    console.log('- isLoggedIn: ', authToken);

    return authToken !== null;
  }

  login(user: User) {
    return this.http
      .post<any>(`${this.apiUrl}/users`, user)
      .subscribe((response: any) => {
        localStorage.setItem('auth_token', response.id);

        this.getUserProfile(response._id).subscribe((response: any) => {
          this.currentUser = response;
          this.router.navigate(['home']);
        });
      });
  }

  // User profile
  getUserProfile(id: any): Observable<any> {
    let api = `${this.endpoint}/user-profile/${id}`;
    return this.http.get(api, { headers: this.headers }).pipe(
      map((res) => {
        return res || {};
      }),
      catchError(this.handleError)
    );
  }

  logout() {
    if (localStorage.removeItem('auth_id') == null) this.router.navigate(['log-in']);
  }
}
