import { Injectable } from '@angular/core';
import { v4 as uuid } from 'uuid';
import { UserLogin, UserSession } from '../interfaces/users.interface';

@Injectable({
  providedIn: 'root'
})
export class SessionService {

  public storageUserSession: UserSession[] = [];

  constructor() {
    this._getUserFromStorage()
  }

  private _getUserFromStorage() {
    if (!localStorage.getItem('storageUserSession')) return;

    this.storageUserSession = JSON.parse(localStorage.getItem('storageUserSession')!)
  }

  private _saveUserSession() {
    localStorage.setItem('userSession', JSON.stringify(this.storageUserSession));
  }

  private _setUserSession(userData:UserSession) {
    this.storageUserSession.push({ userId: uuid(), ...userData })
    this._saveUserSession();
  }

  private _removeUserSession(username:string) {
    this.storageUserSession = this.storageUserSession.filter(storageUsers => storageUsers.username !== username);
  }

  public login(userLogin:UserLogin) {
    this._setUserSession(userLogin.username, userLogin.role);
  }

  public logout(username: string) {
    this._removeUserSession(username);
  }
}
