import { Component, ElementRef, Input, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';

import { SessionService } from '../../services/session.service';
import { UserLogin, UserSession } from 'src/app/interfaces/users.interface';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public userSession: UserSession[] = [];
  public isSubmitted:boolean = false;
  public isValid: boolean = false;

  constructor(private readonly sessionService: SessionService) { }

  public get isLoggin() {
    return this.sessionService.storageUserSession;
  }

  onSubmit(formLogin: UserLogin){
    this.isSubmitted = true;

    if(!formLogin.username || !formLogin.role) {
      this.isValid = false;
    } else {
      this.isValid = true;
    }

    console.log({formLogin}, this.isValid);

  }
}
