import { Component } from '@angular/core';
import { TasksService } from '../../tasks/services/tasks.service';
import { Status, ToDo } from 'src/app/tasks/interfaces/todos.interface';

@Component({
  selector: 'home-page',
  templateUrl: './home-page.component.html'
})
export class HomePageComponent {
  constructor(private tasksService: TasksService){}

  public get todos(): ToDo[] {
    return [...this.tasksService.storageTasks];
  }

  addToDo(todo:string): void {
    let newTodo:ToDo = {
      username: '',
      nameTodo: todo,
      status: 'new'
    }

    this.tasksService.addTask(newTodo);
  }

  removeToDo(id: string): void {
    this.tasksService.deleteToDo(id)
  }

  removeToDoAll(): void {
    this.tasksService.deleteAllToDos();
  }

  updateToDo(id: string, status: Status): void {
    this.tasksService.updateToDo(id, status);
  }

}
