import { Component} from '@angular/core';

import { SessionService } from '../../services/session.service';
import { UserSession } from 'src/app/interfaces/users.interface';
import { FormGroup, NgForm } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public loginForm:FormGroup = new FormGroup({});

  public userSession: UserSession[] = [];
  public isSubmitted:boolean = false;
  public isValid: boolean = false;
  public messageError:string = '';

  constructor(private readonly sessionService: SessionService, private router: Router) {}

  onLogin():void{
    const dataForm = this.loginForm.value
    this.isSubmitted = true;
    this.messageError = '';

    this.messageError += (dataForm.username === '')  ? '- Debe agregar un "Username".<br>': '';
    this.messageError += (dataForm.role === '')      ? ' - Debe selecionar una opción de "Rol"<br>': '';

    if (loginForm.invalid || this.messageError !== '') return;

    this.sessionService.login(dataForm);

  }
}
