import { Component} from '@angular/core';

import { SessionService } from '../../services/session.service';
import { UserSession } from 'src/app/interfaces/users.interface';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {

  public userSession: UserSession[] = [];
  public isSubmitted:boolean = false;
  public isValid: boolean = false;
  public messageError:string = '';

  constructor(private readonly sessionService: SessionService) {
    console.log(this.sessionService.userSession.userId);
  }

  onSubmit(loginForm: NgForm):void{
    this.isSubmitted = true;
    this.messageError = '';

    this.messageError += (loginForm.value.username === '') ? '- Debe agregar un "Username".<br>': '';
    this.messageError += (loginForm.value.role === '') ? ' - Debe selecionar una opción de "Rol"<br>': '';

    if (loginForm.invalid || this.messageError !== '') return;

    this.sessionService.login(loginForm.value);
  }
}
