import { Component } from '@angular/core';
import { TasksService } from '../../services/tasks.service';
import { ToDo } from 'src/app/interfaces/todos.interface';

@Component({
  selector: 'home-page',
  templateUrl: './home-page.component.html'
})
export class HomePageComponent {
  public username: string = 'Aarón';

  constructor(private tasksService: TasksService){}

  public get newTodos(): ToDo[] {
    return [...this.tasksService.getToDosByStatus('new')];
  }

  public get endTodos(): ToDo[] {
    return [...this.tasksService.getToDosByStatus('end')];
  }

  public get removedTodos(): ToDo[] {
    return [...this.tasksService.getToDosByStatus('removed')];
  }

  addToDo(todo:string): void {
    let newTodo:ToDo = {
      username: '',
      nameTodo: todo,
      status: 'new'
    }

    this.tasksService.addTask(newTodo);
  }

  removeToDo(id: string): void {
    this.tasksService.removeToDo(id);
  }

  removeToDoAll(): void {
    this.tasksService.removeAllToDos();
  }

  updateToDo(todo:any): void {
    console.log(todo);

    this.tasksService.updateToDo({...todo});
  }

  updateAllTodos(status:Status): void {
    this.tasksService.updateAllToDos();
  }

  resetAll(): void {
    this.tasksService.resetToDos();
  }

}
<div class="row pt-5">
    <div class="col mx-auto">
        <div class="card">
            <div class="card-body">
                <div class="row">
                    <div class="col-md-2">
                        <b>Bienvenid@: </b>
                    </div>
                    <div class="col-md">
                        {{username}}
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>


<task-new (onDebounce)="addToDo($event)"></task-new>

<task-lists
    [newTodos]="newTodos"
    [endTodos]="endTodos"
    [removedTodos]="removedTodos"
    (onRemoveTodo)="removeToDo($event)"
    (onRemoveAll)="removeToDoAll()"
    (onUpdateToDo)="updateToDo($event)"
    (onUpdateAll)="updateAllTodos($event)"
    (onReset)="resetAll()"
></task-lists>
