import { Injectable } from '@angular/core';
import { v4 as uuid } from 'uuid';
import { UserSession } from '../interfaces/users.interface';

@Injectable({
  providedIn: 'root'
})
export class SessionService {

  public storageUserSession: UserSession[] = [];
  public userSession: UserSession = {
    username: '',
    role: false
  };

  constructor() {
    this._loadUserFromStorage();
  }

  private _loadUserFromStorage():void {
    console.log('_loadUserFromStorage()');

    if (!localStorage.getItem('storageUsersSessions')) return;

    this.storageUserSession = JSON.parse(localStorage.getItem('storageUsersSessions')!)
  }

  private _searchUser(userToFind: UserSession) {
    console.log('_searchUser: ', userToFind);

    this.storageUserSession.filter(storageUser => (storageUser.username === this.userSession.username && storageUser.role === this.userSession.role))



  }

  public login(userLogin: UserSession):void {
    console.log('- login()');

    this._searchUser(userLogin);

  }

  public logout(username: string):void {
    console.log('- logout()')

  }
}
